import React, { useEffect, useRef } from 'react';
import { Chart, registerables } from 'chart.js';
Chart.register(...registerables);

/**
 * SAMPLE patient data. Replace with data fetched from your API.
 */
const sampleJessicaData = {
  name: 'Jessica Taylor',
  age: 29,
  gender: 'Female',
  address: '123 Main St, Anytown, USA',
  insurance: 'Sunshine Assurance',
  bloodPressureReadings: [
    // Example structure for systolic & diastolic data
    { year: 2018, systolic: 120, diastolic: 78 },
    { year: 2019, systolic: 122, diastolic: 80 },
    { year: 2020, systolic: 125, diastolic: 82 },
    { year: 2021, systolic: 128, diastolic: 85 },
    { year: 2022, systolic: 130, diastolic: 86 },
  ],
};

/* 
  SIDEBAR COMPONENT
  Mirrors the left panel with a logo, nav links, and a list of patients.
*/
function Sidebar() {
  return (
    <aside className="sidebar">
      <div className="sidebar__logo">
        <h2>Tech.Care</h2>
      </div>
      <nav className="sidebar__nav">
        <ul>
          <li className="active">Overview</li>
          <li>Patients</li>
          <li>Schedule</li>
          <li>Message</li>
          <li>Transactions</li>
        </ul>
      </nav>

      <div className="sidebar__patients">
        <h3>Patients</h3>
        <ul>
          <li>Emily Williams</li>
          <li>Ryan Johnson</li>
          <li>Brandon Mitchell</li>
          <li>Jessica Taylor</li>
          {/* Add more patients as needed */}
        </ul>
      </div>
    </aside>
  );
}

/*
  TOP BAR COMPONENT
  Mirrors the top bar with a title, search, and user info (e.g., Dr. Jose Simmons).
*/
function TopBar() {
  return (
    <div className="topbar">
      <h1>Patients</h1>
      <div className="topbar__search">
        <input type="text" placeholder="Search..." />
      </div>
      <div className="topbar__user">
        <span>Dr. Jose Simmons</span>
        <img
          src="https://via.placeholder.com/40"
          alt="User"
          className="user-avatar"
        />
      </div>
    </div>
  );
}

/*
  MAIN CONTENT COMPONENT
  This contains the "Diagnosis History" with a Chart, 
  plus some additional diagnostic cards and a sample table for "Diagnostic List".
*/
function MainContent({ patient }) {
  const chartRef = useRef(null);

  useEffect(() => {
    if (patient && chartRef.current) {
      // Prepare data for Chart.js
      const labels = patient.bloodPressureReadings.map((entry) => entry.year);
      const systolicData = patient.bloodPressureReadings.map((entry) => entry.systolic);
      const diastolicData = patient.bloodPressureReadings.map((entry) => entry.diastolic);

      new Chart(chartRef.current, {
        type: 'line',
        data: {
          labels,
          datasets: [
            {
              label: 'Systolic',
              data: systolicData,
              borderColor: '#FF6B6B',
              borderWidth: 2,
              fill: false,
              tension: 0.1,
            },
            {
              label: 'Diastolic',
              data: diastolicData,
              borderColor: '#4ECDC4',
              borderWidth: 2,
              fill: false,
              tension: 0.1,
            },
          ],
        },
        options: {
          responsive: true,
          maintainAspectRatio: false,
          plugins: {
            legend: {
              display: true,
            },
          },
          scales: {
            y: {
              beginAtZero: false,
            },
          },
        },
      });
    }
  }, [patient]);

  return (
    <div className="main-content">
      {/* Diagnosis History */}
      <div className="diagnosis-history">
        <h2>Diagnosis History</h2>
        <div className="blood-pressure-chart">
          <canvas ref={chartRef} id="bpChart"></canvas>
        </div>

        {/* Example KPI cards for the screenshot */}
        <div className="kpi-cards">
