import React, { useEffect, useRef } from 'react';
import { Chart, registerables } from 'chart.js';
import '../styles/MainContent.css';
Chart.register(...registerables);

export default function MainContent({ patient }) {
  const chartRef = useRef(null);
  const chartInstanceRef = useRef(null);

  // Sample data if patient data is not yet integrated
  const bloodPressureData = [
    { month: 'Oct 2023', systolic: 152, diastolic: 98 },
    { month: 'Nov 2023', systolic: 145, diastolic: 94 },
    { month: 'Dec 2023', systolic: 158, diastolic: 102 },
    { month: 'Jan 2024', systolic: 150, diastolic: 96 },
    { month: 'Feb 2024', systolic: 148, diastolic: 94 },
    { month: 'Mar 2024', systolic: 160, diastolic: 98 },
  ];

  // Sample diagnostic list
  const diagnosticList = [
    {
      diagnosis: 'Hypertension',
      description: 'Chronic high blood pressure',
      status: 'Under Observation'
    },
    {
      diagnosis: 'Type 2 Diabetes',
      description: 'Insulin resistance and elevated blood sugar',
      status: 'Cured'
    },
    {
      diagnosis: 'Asthma',
      description: 'Recurrent episodes of bronchial constriction',
      status: 'Inactive'
    }
  ];

  useEffect(() => {
    // Clean up previous chart instance
    if (chartInstanceRef.current) {
      chartInstanceRef.current.destroy();
    }

    if (chartRef.current) {
      const ctx = chartRef.current.getContext('2d');
      chartInstanceRef.current = new Chart(ctx, {
        type: 'line',
        data: {
          labels: bloodPressureData.map(entry => entry.month),
          datasets: [
            {
              label: 'Systolic',
              data: bloodPressureData.map(entry => entry.systolic),
              borderColor: '#E74C8C',
              backgroundColor: '#E74C8C',
              borderWidth: 2,
              tension: 0.4,
              pointRadius: 4,
              pointBackgroundColor: '#fff',
            },
            {
              label: 'Diastolic',
              data: bloodPressureData.map(entry => entry.diastolic),
              borderColor: '#9370DB',
              backgroundColor: '#9370DB', 
              borderWidth: 2,
              tension: 0.4,
              pointRadius: 4,
              pointBackgroundColor: '#fff',
            }
          ]
        },
        options: {
          responsive: true,
          maintainAspectRatio: false,
          plugins: {
            legend: {
              display: true,
              position: 'top',
            },
            tooltip: {
              mode: 'index',
              intersect: false,
            },
          },
          scales: {
            y: {
              beginAtZero: false,
              min: 80,
              max: 170,
              grid: {
                drawBorder: false,
              },
              ticks: {
                stepSize: 10,
              }
            },
            x: {
              grid: {
                display: false,
              }
            }
          },
        }
      });
    }

    return () => {
      if (chartInstanceRef.current) {
        chartInstanceRef.current.destroy();
      }
    };
  }, [bloodPressureData]);

  return (
    <div className="main-content">
      <div className="diagnosis-container">


        <div className="diagnosis-content">
        <h2 className="section-title">Diagnosis History</h2>
        <br />
          <div className="chart-section">
            <div className="chart-header">
              <h3><b>Blood Pressure</b></h3>
              <div className="time-selector">
                <span><b>Last 6 months</b></span>
                <span className="dropdown-icon">▼</span>
              </div>
            </div>

            <div className="bp-chart">
              <canvas ref={chartRef} id="bpChart"></canvas>
            </div>

            <div className="bp-readings">
              <div className="systolic-info">
                <span className="dot systolic"></span>
                <span className="label">Systolic</span>
                <span className="value">160</span>
                <span className="trend">Higher than Average</span>
              </div>

              <div className="diastolic-info">
                <span className="dot diastolic"></span>
                <span className="label">Diastolic</span>
                <span className="value">78</span>
                <span className="trend">Lower than Average</span>
              </div>
            </div>
          </div>

          <div className="stats-cards">
            <div className="stats-card">
              <div className="icon-container respiratory">
                <img src="https://img.icons8.com/color/64/000000/lungs.png" alt="Respiratory Rate" />
              </div>
              <div className="stat-content">
                <h3>Respiratory Rate</h3>
                <p className="stat-value">20 bpm</p>
                <span className="stat-status normal">Normal</span>
              </div>
            </div>

            <div className="stats-card">
              <div className="icon-container temperature">
                <img src="https://img.icons8.com/color/64/000000/temperature.png" alt="Temperature" />
              </div>
              <div className="stat-content">
                <h3>Temperature</h3>
                <p className="stat-value">98.6°F</p>
                <span className="stat-status normal">Normal</span>
              </div>
            </div>

            <div className="stats-card">
              <div className="icon-container heart-rate">
                <img src="https://img.icons8.com/color/64/000000/heart-with-pulse.png" alt="Heart Rate" />
              </div>
              <div className="stat-content">
                <h3>Heart Rate</h3>
                <p className="stat-value">78 bpm</p>
                <span className="stat-status lower">Lower than Average</span>
              </div>
            </div>
          </div>
        </div>
      </div>

      <div className="diagnostic-section">
        <h2 className="section-title">Diagnostic List</h2>
        <table className="diagnostic-table">
          <thead>
            <tr>
              <th>Problem/Diagnosis</th>
              <th>Description</th>
              <th>Status</th>
            </tr>
          </thead>
          <tbody>
            {diagnosticList.map((item, index) => (
              <tr key={index}>
                <td>{item.diagnosis}</td>
                <td>{item.description}</td>
                <td>
                  <span className={`status-badge ${item.status.toLowerCase().replace(/\s/g, '-')}`}>
                    {item.status}
                  </span>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
}
